#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../vitepress@1.4.1_@algolia+client-search@4.24.0_@types+node@22.5.4_@types+react@18.3.5_axios@1._t44nxhivxkk5qotcoc3xnhgq6q/node_modules/vitepress/bin/vitepress.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../vitepress@1.4.1_@algolia+client-search@4.24.0_@types+node@22.5.4_@types+react@18.3.5_axios@1._t44nxhivxkk5qotcoc3xnhgq6q/node_modules/vitepress/bin/vitepress.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../vitepress@1.4.1_@algolia+client-search@4.24.0_@types+node@22.5.4_@types+react@18.3.5_axios@1._t44nxhivxkk5qotcoc3xnhgq6q/node_modules/vitepress/bin/vitepress.js" $args
  } else {
    & "node$exe"  "$basedir/../../../../../vitepress@1.4.1_@algolia+client-search@4.24.0_@types+node@22.5.4_@types+react@18.3.5_axios@1._t44nxhivxkk5qotcoc3xnhgq6q/node_modules/vitepress/bin/vitepress.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
